Object.Field:

        import oracle.odi.mapping.generation.AbstractSyntaxTree.ASTProducerMethod;
		import oracle.odi.domain.mapping.generator.KMGeneratorDelegateRegistry.GeneratorDelegateClass;
		@GeneratorDelegateClass(componentType="AP", descriptionKey="GeneratorDelegateClass.HiveSqoopExportBaseLKM")
        public class HiveSqoopExportBaseLKM extends HiveSqoopBaseLKM {    	
            // class constructor; initialization is done in setBinding method
            public HiveSqoopExportBaseLKM() {}        
            // Declare KM level vars
            protected String option_SQOOP_PARALLELISM
            protected boolean isFromHive, isFromHBase, isFromFile, isToTeradata, isToMsSQL, isToMySQL
            protected srcTechno, destTechno
            protected String srcTechnoName, destTechnoName
            protected collTableName

            public void setBinding( Binding binding ) {
                super.setBinding( binding )
                // Initialize variables
                option_SQOOP_PARALLELISM   = getOption(    "SQOOP_PARALLELISM" )
                srcTechno          = physicalNode.getAPNodeSourceExUnit().getLocation().getTechnology()
                srcTechnoName      = srcTechno.getInternalName()
                destTechno         = physicalNode.getExecutionUnit().getLocation().getTechnology()
                destTechnoName     = destTechno.getInternalName()

                isFromFile         = srcTechnoName.equalsIgnoreCase  ("FILE")
                isFromHive         = srcTechnoName.equalsIgnoreCase  ("HIVE")
                isFromHBase        = srcTechnoName.equalsIgnoreCase  ("HBASE")
                isToTeradata       = destTechnoName.equalsIgnoreCase ("TERADATA")                
                isToMsSQL          = destTechnoName.equalsIgnoreCase ("MICROSOFT_SQL_SERVER")
                isToMySQL          = destTechnoName.equalsIgnoreCase ("MYSQL")

                // Due to a bug in SQOOP the stage table on MySQL and MSSQL must be created in the *data* schema instead of the work schema.
                collTableName      = ( isToMySQL || isToMsSQL ) ?
                                     '<?=odiRef.getObjectName("L", "' + "%COL_PRF" + physicalNode.getCollTableUniquenessDigits() + physicalNode.getStageTableName() + '", "D")?>' :
                                     '<?=odiRef.getObjectName("L", "' + "%COL_PRF" + physicalNode.getCollTableUniquenessDigits() + physicalNode.getStageTableName() + '", "W")?>'
            }

            void updateBinding() {
                // Publish variables into binding to make them available in templates and variableDefText
                def useGeneric      = option_USE_GENERIC_JDBC_CONNECTOR

                mBinding.setVariable( "destJavaUrl",    '''<?=odiRef.getInfo("DEST_JAVA_URL")?>''' )
                mBinding.setVariable( "destJavaDriver", '''<?=odiRef.getInfo("DEST_JAVA_DRIVER")?>''' )
                mBinding.setVariable( "destUserName",   '''<?=odiRef.getInfo("DEST_USER_NAME")?>''' )
                mBinding.setVariable( "destPassword", '''<?=odiRef.getInfo("DEST_PASS")?>''' )            

                def destTechnoName = destTechno.getInternalName()
                def isToOracle     = destTechnoName.equalsIgnoreCase ("ORACLE")
                def isToTeradata   = destTechnoName.equalsIgnoreCase ("TERADATA")
                def isToIbmDb2Udb  = destTechnoName.equalsIgnoreCase ("IBM_DB2_UDB")
                def isToNetezza    = destTechnoName.equalsIgnoreCase ("NETEZZA")                

                mBinding.setVariable( "useGeneric",      useGeneric )             
                mBinding.setVariable( "isFromFile",      isFromFile ) 
                mBinding.setVariable( "isFromHive",      isFromHive ) 
                mBinding.setVariable( "isFromHBase",     isFromHBase ) 
                mBinding.setVariable( "isToMsSQL",       isToMsSQL ) 
                mBinding.setVariable( "isToTeradata",    isToTeradata ) 

                def stageTableName        = generatorContext.getAPNodeCollTableAccessName( physicalNode )
                                            //physicalNode.getStageTableName()     
                def stageTableShortName   = '<?=odiRef.getObjectShortName("L", "' + "%COL_PRF" + physicalNode.getCollTableUniquenessDigits() + physicalNode.getStageTableName() + '", "W")?>'
                def teradataStageTableShortName = '<?=odiRef.getObjectShortName("L", "' + "%COL_PRF" + physicalNode.getCollTableUniquenessDigits() + physicalNode.getStageTableName() + '_TD", "W")?>'
                def teradataStageTableName      = '<?=odiRef.getObjectName     ("L", "' + "%COL_PRF" + physicalNode.getCollTableUniquenessDigits() + physicalNode.getStageTableName() + '_TD", "W")?>'

                // Get target table name, must be target-side qualified (no schema)
                // Get COLL_SHORT_NAME                                                           
                def collTableShortName = '<?=odiRef.getObjectShortName("L", "' + "%COL_PRF" + physicalNode.getCollTableUniquenessDigits() + physicalNode.getStageTableName() + '", "W")?>'
        	    def sqoopTargetTable   = ""
                def sqoopTargetSchema  = ""
                if ( isToMsSQL || isToMySQL ) { 
                    // for these technologies we must use the unqualified table name to compensate SQOOP bug ...
        	        sqoopTargetTable  = collTableShortName
                    sqoopTargetSchema = '''<?=odiRef.getInfo("DEST_SCHEMA")?>'''
                } else {
                    sqoopTargetTable = collTableName
                }
                mBinding.setVariable( "sqoopTargetTable",  sqoopTargetTable ) 
                mBinding.setVariable( "sqoopTargetSchema", sqoopTargetSchema ) 

                mBinding.setVariable( "option_SQOOP_PARALLELISM",                     option_SQOOP_PARALLELISM )
                mBinding.setVariable( "tempPath", getQuotedString( getOption( "TEMP_DIR" ) ) ) 
                if ( isFromFile ) {
                    mBinding.setVariable( "sourceDir",  '''<?=odiRef.getQuotedString( odiRef.getInfo("SRC_HADOOP_NAME_NODE_URI") +"/" + odiRef.getInfo( "SRC_SCHEMA" )) ?>''')
                } else {
                    // mBinding.setVariable( "sourceDir", getQuotedString( getOption( "MAPRED_OUTPUT_BASE_DIR" )  + "/" + stageTableName ) )                
                    // Because of $ sign in the directory name Clean up directory tasks were failing to execute.
                    // Changing to physicalNode.getSQLAccessName() which is not yet translated and does not contain C$ prefix.
                    // This is a temporary directory and the name is not that important as far as it is unique.
                    String rawSourceDir = getOption( "MAPRED_OUTPUT_BASE_DIR" ) + "/" + physicalNode.getSQLAccessName() 
                    mBinding.setVariable( "sourceDir", getQuotedString( rawSourceDir ) )
                    mBinding.setVariable( "unquotedSourceDir", rawSourceDir )
                }
                def workTableOptions = getOption("WORK_TABLE_OPTIONS")
                def workTableType    = (isToTeradata)?getOption("TERADATA_WORK_TABLE_TYPE"):""
                if ( workTableOptions.length() == 0 ) {
                    if ( isToOracle )
                        workTableOptions = "NOLOGGING";
                    if ( isToIbmDb2Udb )
                        workTableOptions = "NOT LOGGED INITIALLY";
                    if ( isToTeradata )
                        workTableOptions = "no fallback, no before journal, no after journal";            
                }
                stgTableAST.tableType    = workTableType
                stgTableAST.tableOptions = workTableOptions

                // Due to a bug in SQOOP the stage table on MySQL and MSSQL must be created in the *data* schema instead of the work schema.
                // So we need to override the standard work table name.
                if ( isToMySQL || isToMsSQL )
                    stgTableAST.getTable().setCustomText( collTableName )

                // Some Teradata specific vars
                if ( isToTeradata ) {
                    mBinding.setVariable( "destWorkSchemaName",          '''<?=odiRef.getInfo("DEST_WORK_SCHEMA")?>''' ) 
                    mBinding.setVariable( "stageTableShortName",         stageTableShortName ) 
                    mBinding.setVariable( "teradataStageTableShortName", teradataStageTableShortName ) 
                    mBinding.setVariable( "teradataStageTableName",      teradataStageTableName ) 
                    mBinding.setVariable( "option_TERADATA_OUTPUT_METHOD", getOption("TERADATA_OUTPUT_METHOD") ) 
                }

                // No need for a Hive session setup
                prepareHiveSession = false

                // Validate target technology
                if ( !( isToOracle || isToTeradata || isToMsSQL || isToMySQL || isToIbmDb2Udb || isToNetezza || useGeneric ) ) {
                    throw new OdiKMException(
                        "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: This KM does not have specific connector support for target technology {0}. Please consider using USE_GENERIC_JDBC_CONNECTOR = true.", 
                        [ destTechnoName ] as String[] )
                }

                // Validate source technology
                if (!(isFromFile || isFromHive || isFromHBase )) {
                    throw new OdiKMException(
                        "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: Source technology {0} is not supported.", 
                        [ srcTechnoName ] as String[] )
                }

                super.updateBinding()
            }

			@ASTProducerMethod(processingType=ProcessingType.SOURCE, producedASTType=SqlQuery.class, styleKey="ProcessingStyleKey.Hive.SELECT")     
            public AbstractSyntaxTree SELECT() {
                if ( isToMySQL || isToMsSQL )
                    return LKM_SQL_to_SQL_v2__SELECT( collTableName )
                else
                    return LKM_SQL_to_SQL_v2__SELECT()
            }            

		    @ASTProducerMethod(processingType=ProcessingType.TARGET, producedASTType=NullTarget.class, styleKey="ProcessingStyleKey.Hive.INSERT")      
            public AbstractSyntaxTree INSERT( SqlQuery input ) {                                              
                stgTableAST = null

                // Publish variables into binding to make them available in templates and variableDefText
                updateBinding()
                return new NullTarget()
            }

        } // End of class definition
