Object.Field:
begin
    dbms_stats.gather_table_stats(
	ownname => '<?=snpRef.getInfo("DEST_WORK_SCHEMA") ?>',
	tabname => '<?=snpRef.getObjectName("L", "%INT_PRFDT_MAPPING", "W").replaceAll(odiRef.getInfo( "DEST_WORK_SCHEMA" )+".","")?>',
	estimate_percent => dbms_stats.auto_sample_size
    );
end;


/* FLOW CONTROL CREATE THE iNDEX ON THE I$TABLE */
create index 	<?=snpRef.getObjectName("L", "%INT_PRFDT_MAPPING_IDX", "W") ?>
on	<?=snpRef.getObjectName("L", "%INT_PRFDT_MAPPING", "W") ?> (ID)


/* FLOW CONTROL CREATE INDEX ON THE E$TABLE */
create index 	<?=snpRef.getObjectName("L", "%ERR_PRFDT_MAPPING_IDX", "W") ?>
on	<?=snpRef.getObjectName("L", "%ERR_PRFDT_MAPPING", "W") ?> (ODI_ROW_ID)

merge into <?=snpRef.getObjectName("L", "DT_MAPPING", "DT_TCL", "", "D") ?> T using <?=snpRef.getObjectName("L", "%INT_PRFDT_MAPPING", "W") ?> S on ( T.ID=S.ID ) when matched then update set T.NAME = S.NAME, T.ORIGINAL_NAME = S.ORIGINAL_NAME, T.MODULE = S.MODULE, T.MAX_NUMBER_OF_ERRORS = S.MAX_NUMBER_OF_ERRORS, T.ANALIZE_TARGET = S.ANALIZE_TARGET, T.DEFAULT_OPERATING_MODE = S.DEFAULT_OPERATING_MODE
when not matched then insert ( T.ID, T.NAME, T.ORIGINAL_NAME, T.MODULE, T.MAX_NUMBER_OF_ERRORS, T.ANALIZE_TARGET, T.DEFAULT_OPERATING_MODE
) values ( S.ID, S.NAME, S.ORIGINAL_NAME, S.MODULE, S.MAX_NUMBER_OF_ERRORS, S.ANALIZE_TARGET, S.DEFAULT_OPERATING_MODE
)
